import java.util.Scanner;


public class Problem3 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner userInput = new Scanner (System.in);
		
		int num;
		int sum;
		
		num = userInput.nextInt();
		while (num <= num);
		{ num = num + 1;
			++num;
			sum= num + num;
		}
	
	
	System.out.println ("sum = " + sum);
	
	
	
	}

}
